import { Link } from "react-router-dom"
import { headercategory } from "../HeaderCategory"
import CartWidget from "../CartWidget"
import CartProvider from "../providers/CartProvider"
import MenuCard from "../Menucard"
import { loadMenu } from "../../store/slices/menuSlice"
import { useDispatch, useSelector } from "react-redux"
import { useEffect } from "react"

const Header = () => {
    const dispatch = useDispatch();
  const menu = useSelector(state => state.menu)


  useEffect(() => {
    dispatch(loadMenu())
  }, [])
let filter="fried roll"   ;
    return (


        <div className=" text-xl mx-auto grid  w-full grid-cols-2 md:grid-cols-3 lg:grid-cols-4 xl:grid-cols-5  container flex  justify-items-center  sticky mx-auto ">
            {headercategory.map((item) => (
            <div className="hover:bg-orange-400 border-8 border-solid border-indigo-600 container text-center rounded-xl p-2 ">
                <button onClick={() =>{

                  filter=item.categ;
                    //console.log(filter);
          menu.items.map((item) => (
            <MenuCard item={item} filter={filter}/>
          ))
                }}>{item.name}
                </button>

            </div>
            ))

            }
            <CartProvider>
            <CartWidget />
          </CartProvider>
        </div>
    )
}
export default Header
